from typing import List


def exist(board: List[List[str]], word: str) -> bool:
    stack = list()
    visited = set()

    for stack in find_first_element(board, word, stack, visited):
        while stack:
            to_check, i, j, count, visited = stack.pop()

            if to_check == word[count]:
                if count == len(word) - 1:
                    return True
                else:
                    new_iter = set(visited)
                    new_iter.add(tuple([i, j]))
                    stack = add_neighbours(i, j, count + 1, stack, board, new_iter)

    return False


def find_first_element(board, word, stack, visited):
    for i in range(len(board)):
        for j in range(len(board[0])):
            if board[i][j] == word[0]:
                if len(word) == 1:
                    yield [(word, i, j, 0, visited)]
                else:
                    new_iter = set(visited)
                    new_iter.add(tuple([i, j]))
                    yield add_neighbours(i, j, 1, stack, board, new_iter)


def add_neighbours(i, j, count, stack, board, visited):
    to_add = {(i + 1, j), (i - 1, j), (i, j + 1), (i, j - 1)}
    if i == 0:
        to_add.remove((i - 1, j))
    if i == len(board) - 1:
        to_add.remove((i + 1, j))
    if j == 0:
        to_add.remove((i, j - 1))
    if j == len(board[0]) - 1:
        to_add.remove((i, j + 1))

    for i, j in to_add:
        if (i, j) not in visited:
            stack.append((board[i][j], i, j, count, visited))
    return stack


